choose.when:
  			select 

		  	from shenbogame_record force index(${params.timeType})
			where ${params.timeType} BETWEEN #{params.startTime} and #{params.endTime}

				and cagent = #{params.cagentName}





						and gameprovidercode = 'TGP'



						and gameprovidercode = 'OG'







						and username in ${params.userName}





				  		and username = #{params.name}



			ORDER BY ${params.timeType} desc 
			limit
			${(page-1)*limit},
			#{limit,jdbcType=INTEGER};

choose.otherwise:
  			select username,
	  			count(username) as id,
	  			sum(abs(riskamt)) as riskamt,
	  			sum(winloss) as winloss
  			from shenbogame_record force index(${params.timeType})
  			where ${params.timeType} BETWEEN #{params.startTime} and #{params.endTime}

				and cagent = #{params.cagentName,jdbcType=VARCHAR}





						and gameprovidercode = 'TGP'



						and gameprovidercode = 'OG'







						and username in ${params.userName}





				  		and username = #{params.name}



			GROUP BY username
			limit
			${(page-1)*limit},
			#{limit,jdbcType=INTEGER};

choose.when:
  			select 'total' as userName,
		  		count(id) as noteNum,
	  			sum(abs(riskamt)) as betAmount,
	  			sum(winloss) as netAmount,
	  			sum(abs(riskamt)) as validBetAmount,
	  			count(id) as validNoteNum
		  	from shenbogame_record force index(${params.timeType})
			where ${params.timeType} BETWEEN #{params.startTime} and #{params.endTime}

				and cagent = #{params.cagentName,jdbcType=VARCHAR}





						and gameprovidercode = 'TGP'



						and gameprovidercode = 'OG'







						and username in ${params.userNames}






				  		and username = #{params.name}

choose.otherwise:
  			select user_name as userName,
		  		sum(note_num) as noteNum,
		  		sum(bet_amount) as betAmount,
	  			sum(net_amount) as netAmount,
	  			sum(bet_amount) as validBetAmount,
	  			sum(note_num) as validNoteNum
		  	from shenbogame_platform_report
		  	where bet_time BETWEEN #{params.nextStartDay} and #{params.lastEndDay}
		  		and type = 0

					and cagent = #{params.cagentName}





							and user_name in ${params.userNames}






					  		and user_name = #{params.name}



				group by userName

					union all
					select username as userName,
						count(id) as noteNum,
				  		sum(abs(riskamt)) as betAmount,
			  			sum(winloss) as netAmount,
			  			sum(abs(riskamt)) as validBetAmount,
	  					count(id) as validNoteNum
					from shenbogame_record 
					where ${params.timeType} BETWEEN #{params.startTime} and #{params.startTime1}

						and cagent = #{params.cagentName,jdbcType=VARCHAR}





								and username in ${params.userNames}






						  		and username = #{params.name}



					group by userName


					union all
					select username as userName,
						count(id) as noteNum,
						sum(abs(riskamt)) as betAmount,
			  			sum(winloss) as netAmount,
			  			sum(abs(riskamt)) as validBetAmount,
	  					count(id) as validNoteNum
					from shenbogame_record 
					where ${params.timeType} BETWEEN #{params.endTime1} and #{params.endTime}

						and cagent = #{params.cagentName,jdbcType=VARCHAR}





								and username in ${params.userNames}






						  		and username = #{params.name}



					group by userName
