mapper.insert:
insert into t_activity (id, cagent, cid, 
  type_id, name, type, 
  status, ruid, stime, etime, 
  create_time, update_time, cuid
  )
values (#{id,jdbcType=BIGINT}, #{cagent,jdbcType=VARCHAR}, #{cid,jdbcType=INTEGER}, 
  #{typeId,jdbcType=INTEGER}, #{name,jdbcType=VARCHAR}, #{type,jdbcType=TINYINT}, 
  #{status,jdbcType=BIT}, #{ruid,jdbcType=VARCHAR}, #{stime,jdbcType=BIGINT}, #{etime,jdbcType=BIGINT}, 
  #{createTime,jdbcType=BIGINT}, #{updateTime,jdbcType=BIGINT}, #{cuid,jdbcType=VARCHAR}
  )

mapper.select:
select u.uid, u.cid, u.id, u.username, t.realname, u.activity_name, u.activity_amount, u.carete_time, u.flag, u.verifier, u.rmk, u.verify_time, u.activity_number from t_user_acitivity_record u left join t_user t on t.uid = u.uid WHERE 1=1
and u.flag = 0
and u.flag = 1
and u.flag = 2
and u.carete_time >= #{params.startDate}
and u.carete_time <= #{params.endTime}
and u.uid = (select uid from t_user where username = #{params.username})
and u.cid = #{params.cagent}
and u.cid = (select id from t_cagent where cagent = #{params.cagentName})
ORDER BY carete_time DESC limit ${(page-1)*limit},#{limit}

select count(1) from t_user_acitivity_record u left join t_user t on t.uid = u.uid WHERE 1=1
and u.flag = 0
and u.flag = 1
and u.flag = 2
and u.carete_time >= #{params.startDate}
and u.carete_time <= #{params.endTime}
and u.uid = (select uid from t_user where username = #{params.username})
and u.cid = #{params.cagent}
and u.cid = (select id from t_cagent where cagent = #{params.cagentName})
