mapper.select:
select

from t_signin_record
where 1 = 1

  and username = #{params.username}


  and cid = select id from t_cagent where cagent = #{params.cagent}

limit ${(page-1)*limit},#{limit}

select
count(1)
from t_cagent
where 1=1

  and username = #{params.username}


  and cid = select id from t_cagent where cagent = #{params.cagent}


  and addtime > #{params.startTime}


  and addtime < #{params.endTime}
