mapper.select:
SELECT r.id AS id, r.uid AS uid, r.type AS type, r.wtype AS wtype, r.amount AS amount, r.old_money AS old_money, r.new_money AS new_money, r.uptime AS uptime, r.upuid AS upuid, r.rmk AS rmk, u.username AS username, t1.login_name AS updateName FROM t_user_wallet_log r INNER join t_user u on r.uid = u.uid left join `user` t1 on r.upuid = t1.id where r.uid = u.uid
and r.uptime >= #{params.startTime}
and r.uptime <= #{params.endTime}
AND r.type=#{params.type}
AND u.username=#{params.username}
AND u.cid=#{params.cid}
AND u.top_id=#{params.top_id}
ORDER BY r.uptime DESC LIMIT ${(page-1)*limit},#{limit}

SELECT
count(1) as total,
IFNULL(sum(amount),0) as amount
FROM
t_user_wallet_log r,t_user u
where r.uid = u.uid

     and r.uptime >= #{startTime} 


     and r.uptime <= #{endTime} 


    AND r.type=#{type}


    AND u.username=#{username}


    AND u.cid=#{cid}


    AND u.top_id=#{top_id}
